import React from "react";
import Button from '@material/react-button';

import './About2.scss';

import logoColor from '../assets/aprendiCODEColor.png';
import Card from 'react-bootstrap/Card';
import CardDeck from 'react-bootstrap/CardDeck';
import Kemley from '../assets/Kemley.jpeg';
import Sabrina from '../assets/Sabrina.jpg';
import Esteban from '../assets/Esteban.png';
import chart from "../assets/chart.png";
import logo from '../assets/aprendiCODEColor.png';
import ball from '../assets/ball.png';
import book from '../assets/about.png';

export class About2 extends React.Component {
    render() {
        return (
            <div className="about">
                <div className="who-we-are">
                    <div className="card">
                        <img className="card-img" src={Kemley} />
                    </div>
                    <div className="card">
                        Sabs
                    </div>
                    <div className="card">
                        Sabs
                    </div>
                </div>
                <div className="bienvenido">
                    <span className="bienvenido-title">BIENVENIDO A <img className="bienvenido-logo" src={logoColor} alt="logo"></img></span>
                    <p className="bienvenido-p">Este programa es para niños hispanohablantes que quieren aprendan a codificar con una experiencia interactiva.
              Hecho para niños de 8-12 años.</p>
                </div>
            </div>
        );
    }
}
